import { TodoAction, TodoActionTypes } from '../types/typesTodo'
import { Dispatch } from 'redux'
import { ITodo } from '../../models/interface/interface'
import { store } from '../index'

export const getTodos = () => {
  const todos = store.subscribe()
  return ({ type: TodoActionTypes.FETCH_TODOS_SUCCESS })
}

export const createTodo = (todo: ITodo) => {
  return ({ type: TodoActionTypes.CREATE_TODO_SUCCESS, todo })
}
