import React, {FC} from "react";
import { ITask } from "../../models/interface/interface";
import './style.css'

interface TaskProps {
    todo: ITask
    btnTodoRemove: (e: React.MouseEvent<HTMLButtonElement>) => void
}

const Task: FC<TaskProps> = ({todo, btnTodoRemove}) => {

    return (
            <div className='task'>
                    <input type="checkbox" checked={todo.isCheck}/>
                    <p>{todo.title}</p>
                    <button className='btn-edit'>Edit</button>
                    <button id={todo._id} className='btn-trash' onClick={btnTodoRemove}>Remove</button>
            </div>
    )
}
export default Task
