import React, { useEffect, useState } from "react";
import TodoService from "../../service/todoService/TodoService";
import { PopupContainer } from '../todo-edit-popup/popup-container';
import { TodosComponent } from "./todos-component";
import { ITask } from "../../models/interface/interface";
import './style.css';

interface TodosComponentProp {
    btnEditTodo: (id: ITask["_id"]) => void

}

interface TodosContainerProp {
    fetchTodos: () => void
    todos: ITask[]
    btnTodoRemove: (id: ITask["_id"]) => void
    changeTodoHandler: (body: ITask, id: string) => void
    prevTodoValue: (id: string, value: string) => void
    todoIsCheck: (id: ITask["_id"]) => void
}

const TodosContainer = ({
        fetchTodos,
        todos,
        btnTodoRemove,
        changeTodoHandler,
        prevTodoValue
        }: TodosContainerProp): JSX.Element => {

    const [prevValueTitle, setTodoTitlePrev] = useState<string>('')
    const [indexTodoChanged, setTodoId] = useState<string>('')

    useEffect( () => {
        fetchTodos()
    }, [])

    const btnEditTodo = (id: string | undefined) => {
        console.log(id)

            // todos.forEach(todo => {
            //     if (todo._id === ) {
            //         const value = todo.title
            //         prevTodoValue(, value)
            //         setTodoTitlePrev(value)
            //         setTodoId()
            //     }
            // })
    }

    const toggleIsCheck = async (body: ITask, id: string): Promise<void> => {
        try {
            const updatedTodo: ITask = await TodoService.updateTodo(body, id)
            changeTodoHandler(body, id)
            console.log(updatedTodo, 'I`M FUNCTION UPDATE')
        } catch (e) {
            console.log('Error in toggleIsCheck function', e)
        }
    };

    const todoIsCheck = (id: string) => {
        todos.map(async (todo) => {
            if (todo._id === id) {
                const body = {
                    id,
                    title: todo.title,
                    isCheck: todo.isCheck
                }
                console.log(body)
                try {
                    await toggleIsCheck(body, id)
                } catch (e) {
                    console.log('Error on todoIsCheck function', e)
                }
            }
        })
    }

    return (
        <>
            <TodosComponent todos={todos} btnTodoRemove={btnTodoRemove} btnEditTodo={btnEditTodo} todoIsCheck={todoIsCheck} />
            <PopupContainer changeTodoHandler={changeTodoHandler} prevTodoValue={prevTodoValue} indexTodoChanged={indexTodoChanged} />
        </>
    )
}

export default TodosContainer
