import { TodoAction, TodoActionTypes, TodoState } from '../types/typesTodo'
import { ITodo } from '../../models/interface/interface'

const initialState: TodoState = {
  books: []
}

export const todoReducer = (state = initialState, action: TodoAction): TodoState | undefined => {
  switch (action.type) {
    case TodoActionTypes.FETCH_TODOS:
      return { books: [] }
    case TodoActionTypes.FETCH_TODOS_SUCCESS:
      return { books: action.payload }
    case TodoActionTypes.CREATE_TODO_SUCCESS:
      return { ...state, books: [...state.books, action.payload] }
    case TodoActionTypes.DELETE_TODO_SUCCESS:
      return { ...state, books: [...state.books.filter(todo => action.payload._id !== todo._id)] }
    case TodoActionTypes.CHANGE_TODO_STATE:
      return {
        ...state,
        // eslint-disable-next-line array-callback-return
        books: [...state.books.map((todo) => {
          const { _id, title, isCheck } = todo
        })]
      }
    default:
      return state
  }
}
