import React, {FC, useEffect, useState} from 'react';
import {ITask} from "./components/interface/interface";
import httpQuery from "./components/httpQuery/httpQuery";
import TasksContainer from "./components/tasks-container/tasks-container";

const App: FC = () => {
  const [tasks, setTask] = useState<ITask[]>([])

  useEffect(() => {

    getAllTasks()
  }, [])

  async function getAllTasks(): Promise<ITask[] | void>  {
    try {
      const response = await httpQuery.get<ITask[]>('/allTasks')
      setTask(response.data)
    } catch (e) {
      console.log('I`M Error on GET query!', e)
    }
  }
  return (
    <div>
      <TasksContainer tasks={tasks} />
    </div>

  );
}
export default App;
