import React, { useEffect, useState } from "react";
import TodoService from "../../service/todoService/TodoService";
import { PopupContainer } from '../todo-edit-popup/popup-container';
import { TodosComponent } from "./todos-component";
import { TodoForm } from "../todo-form/todo-form";
import { ITask } from "../../models/interface/interface";
import './style.css';

interface Event {
    target: EventTarget | Element
    preventDefault(): void
}
interface TodosContainerProp {
    fetchTodos: () => void
    newTaskHandler: (body: ITask) => void
    todos: ITask[]
    btnTodoRemove: (e: React.MouseEvent<HTMLButtonElement>) => Promise<void>
    changeTodoHandler: (body: ITask, id: string) => void
    prevTodoValue: (id: string, value: string) => void
}

const TodosContainer = ({
        fetchTodos,
        newTaskHandler,
        todos,
        btnTodoRemove,
        changeTodoHandler,
        prevTodoValue
        }: TodosContainerProp): JSX.Element => {

    // const [todos, setTodos] = useState<ITask[]>([])
    //
    const [prevValueTitle, setTodoTitlePrev] = useState<string>('')
    const [indexTodoChanged, setTodoId] = useState<string>('')
    //
    // const fetchTodos = async (): Promise<void> => {
    //     try {
    //         //todo typisation utility types
    //         const getTodos: any = await TodoService.getAllTodos()
    //         console.log(getTodos)
    //         setTodos(getTodos)
    //     } catch (e) {
    //         console.log('Error in fetchTodos', e)
    //     }
    // }
    //
    useEffect( () => {
        fetchTodos()
    }, [])

    // const newTaskHandler = (newTask: ITask) => {
    //     setTodos([newTask, ...todos])
    // }

    // const changeTodoHandler = (changeTodo: ITask, id: string) => {
    //    todos.map((todo, index) => {
    //        if(id === todo._id) {
    //            const prevArr = todos.splice(index, 1, changeTodo)
    //            const arr = [...todos]
    //            setTodos(arr)
    //        } else {
    //            console.log('не сработало')
    //        }
    //    })
    // }

    // const deleteTodo = async (id: string): Promise<ITask | void> => {
    //     try {
    //         const todo: ITask = await TodoService.deleteTodo(id)
    //         const newArrTodos = todos.filter(task => {
    //            return task._id !== todo._id
    //         })
    //         setTodos(newArrTodos)
    //     } catch (e) {
    //         console.log('Error in function deleteTodo', e)
    //     }
    // }

    // const btnTodoRemove = async (event: Event) => {
    //     event.preventDefault()
    //     if ("id" in event.target) {
    //         const id = event.target.id
    //         try {
    //             await deleteTodo(id)
    //         } catch (e) {
    //             console.log('Error in function deleteTodo', e)
    //         }
    //         console.log(id)
    //     }
    // }

    //todo get todo id instead of event
    const btnEditTodo = (e: Event) => {
        e.preventDefault()
        if('id' in e.target) {
            const id = e.target.id
            todos.forEach(todo => {
                if (todo._id === id) {
                    const value = todo.title
                    prevTodoValue(id, value)
                    setTodoTitlePrev(value)
                    setTodoId(id)
                }
            })
        }
    }

    const toggleIsCheck = async (body: ITask, id: string): Promise<void> => {
        try {
            const updatedTodo: ITask = await TodoService.updateTodo(body, id)
            console.log(updatedTodo)
        } catch (e) {
            console.log('Error in ChangeTodo function', e)
        }
    };

    const todoIsCheck = async (e: any) => {
        e.preventDefault()
        const target = e.target.checked
        const index = e.target.id
        const title = e.target.value
        const body: any = {
            title: title,
            isCheck: target
        }
        console.log(body)
        try {
            await toggleIsCheck(body, index)
        } catch (e) {
            console.log('Error on toggleIsCheck function', e)
        }
    }

    // const prevTodoValue = (id: string, value: string) => {
    //     const body = {
    //         _id: id,
    //         title: value
    //     }
    //     console.log(body)
    // }

    return (
        <>
            <TodoForm newTaskHandler={newTaskHandler} />
            <TodosComponent todos={todos} btnTodoRemove={btnTodoRemove} btnEditTodo={btnEditTodo} todoIsCheck={todoIsCheck}/>
            <PopupContainer changeTodoHandler={changeTodoHandler} prevTodoValue={prevTodoValue} prevValue={prevValueTitle} indexTodoChanged={indexTodoChanged} todoIsCheck={todoIsCheck}/>
        </>
    )
}

export default TodosContainer
