import React, { useEffect, useState } from "react";
import TodoService from "../../service/todoService/TodoService";
import { PopupContainer } from '../todo-edit-popup/popup-container';
import { TodosComponent } from "./todos-component";
import { ITask } from "../../models/interface/interface";
import './style.css';

interface TodosContainerProp {
    fetchTodos: () => void
    todos: ITask[]
    // btnTodoRemove: (id: ITask["_id"]) => void
    // changeTodoHandler: (body: ITask, id: ITask["_id"]) => void
    // btnEditTodo: (id: ITask["_id"]) => void
    setTodos: (body: ITask[]) => void
}

const TodosContainer = ({todos, setTodos}: TodosContainerProp): JSX.Element => {

    const EditTodo = (title: ITask["title"], id: ITask["_id"]) => {
        const value = ''
        const body = {
            id,
            title: value
        }

    }

    const toggleIsCheck = async (body: ITask, id: ITask["_id"]): Promise<void> => {
        try {
            const updatedTodo = await TodoService.updateTodo(body, id)
            changeTodoHandler(updatedTodo, id)
        } catch (e) {
            console.log('Error in toggleIsCheck function', e)
        }
    };

    const todoIsCheck = async (title: ITask["title"] ,isCheck: ITask["isCheck"], id: ITask["_id"]) => {
        const body = {
            _id: id,
            title,
            isCheck: !isCheck
        }
        changeTodoHandler(body, id)
        console.log(body)
        console.log(todos)
        try {
             await toggleIsCheck(body, id)
        } catch (e) {
            console.log(e)
        }
    }

    const changeTodoHandler = (changeTodo: ITask, id: ITask["_id"]) => {
        todos.map((todo, index) => {
            if(id === todo._id) {
                const prevArr = todos.splice(index, 1, changeTodo)
                const arr = [...todos]
                setTodos(arr)
            } else {
                console.log('Problem in function changeTodoHandler')
            }
        })
    }

    const changeCheck = (body: ITask) => {
        console.log(body)
    }

    //todo move to todo container
    const deleteTodo = async (id: ITask["_id"]): Promise<ITask | void> => {
        try {
            const todo: ITask = await TodoService.deleteTodo(id)
            const newArrTodos = todos.filter(task => {
                return task._id !== todo._id
            })
            setTodos(newArrTodos)
        } catch (e) {
            console.log('Error in function deleteTodo', e)
        }
    }
    const TodoRemove = async (id: ITask["_id"]) => {

        try {
            await deleteTodo(id)
        } catch (e) {
            console.log('Error in function deleteTodo', e)
        }
    }

    return (
        <>
            <TodosComponent todos={todos} TodoRemove={TodoRemove} EditTodo={EditTodo} todoIsCheck={todoIsCheck} />
            <PopupContainer changeTodoHandler={changeTodoHandler} />
        </>
    )
}

export default TodosContainer
