import React, { useEffect, useState } from 'react';
import {ITask} from "./components/interface/interface";
import TaskAdd from "./components/task-add/task-add";
import TasksContainer from "./components/tasks-container/tasks-container";
import TaskApi from "./components/TaskApi/TaskApi";

const App = () => {

  const [task, setTask] = useState<ITask[]>([])


  useEffect(() => {
     const fetchTask = async (): Promise<ITask[] | void> => {
       try {
         const response: any = await new TaskApi().getAllTasks()
         setTask(response)
           await fetchTask()
         console.log(response)
       } catch (e) {
         console.log('qwe', e)
       }
     }
  }, [])

  return (
    <div>
        <TaskAdd/>
      <TasksContainer tasks={task}/>
    </div>

  );
}
export default App;
