import React from "react";
import {ITask} from "../../models/interface/interface";

interface PopupComponentProp {
    todo: ITask
    popupBtnHandler: (id: ITask["_id"]) => void
    popupInputHandler: (e: React.ChangeEvent<HTMLInputElement>) => void
}

export const PopupComponent = ({ popupBtnHandler, popupInputHandler, todo }: PopupComponentProp): JSX.Element => {
    return (
        <div>
            <input type="text" placeholder='Title' onChange={popupInputHandler} />
            <button type='submit' onClick={() => popupBtnHandler(todo._id)}>Edit</button>
        </div>
    )
}
