import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import TodoService from '../../service/todoService/TodoService'
import { TodosContainer } from '../todos-container/todos-container'
import { ITodo } from '../../models/interface/interface'
import { TodoFormContainer } from '../todo-form/todo-form-container'
import { fetchTodos } from '../../store/action-creators/action-creator'
import { store } from '../../store'
import { getTodos } from '../../store/action-creators/action'

export const MainContainer = (): JSX.Element => {
  const [todos, setTodos] = useState<ITodo[]>([])
  const dispatch = useDispatch()

  // @ts-ignore
  // const { books } = useSelector(state => state.books)
  // console.log(books)
  const arrTodos: ITodo[] = store.getState().books?.books
  const subscribe = store.subscribe(() => store.getState())
  console.log(arrTodos, subscribe(), store.getState())
  // const newTaskHandler = (newTask: ITodo) => {
  //   setTodos([newTask, ...todos])
  // }
  useEffect(() => {
    dispatch(getTodos())
  }, [])
  return (
            <>
                <TodoFormContainer /* newTaskHandler={newTaskHandler} */ />
                <TodosContainer
                    fetchTodos={subscribe}
                    todos={arrTodos}
                    setTodos={setTodos}
                />
            </>
  )
}
