import React, { useEffect, useState } from 'react';
import {ITask} from "./components/interface/interface";
import TaskAdd from "./components/task-add/task-add";
import TasksContainer from "./components/tasks-container/tasks-container";
import TaskApi from "./components/TaskApi/TaskApi";


const App = () => {

  const [tasks, setTasks] = useState<ITask[]>([])

    const fetchTask = async (): Promise<ITask[] | void> => {
        try {
            const response: any = await new TaskApi().getAllTasks()
            setTasks(response)
            console.log(response)
        } catch (e) {
            console.log('qwe', e)
        }
    }


  useEffect(() => {
    fetchTask();
  }, [])

    //todo handler for new task
    const newTaskHandler = (newTask: any) => {
      setTasks(newTask)
    }

  return (
    <div>
        <TaskAdd handler={newTaskHandler(tasks)}/>
      <TasksContainer tasks={tasks}/>
    </div>

  );
}
export default App;
