import axios, {AxiosResponse} from "axios";
import {ITask} from "../interface/interface";

const http = axios.create({
    baseURL: 'http://localhost:8080',
    headers: {'Content-Type': 'application/json'}
})

class TaskApi {

     getAllTasks = async (): Promise<ITask[] | void> => {
        try {
            const response: AxiosResponse = await http.get('/allTasks')
            return await response.data.data
        } catch (e) {
            console.log('Error GET query', e)
        }
}
    createNewTask = async (body: ITask) => {
         try {
             const response: AxiosResponse = await http.post('/addTask', body)
             return await response
         } catch (e) {
             console.log('Error POST query', e)
         }
    }
}

export default TaskApi
