import { TodoAction, TodoActionTypes } from '../types/typesTodo'
import { ITodo } from '../../models/interface/interface'
import { useDispatch } from 'react-redux'

export class actionsService {
   dispatch = useDispatch()

   fetchTodos = () => {
     return this.dispatch<TodoAction>({ type: TodoActionTypes.FETCH_TODOS_SUCCESS, payload: [] })
   }

   createTodo = (todo: ITodo) => {
     const dispatch = useDispatch()
     return dispatch({ type: TodoActionTypes.CREATE_TODO_SUCCESS, payload: todo })
   }

   deleteTask = (todo: ITodo['_id']) => {
     return ({ type: TodoActionTypes.DELETE_TODO_SUCCESS, payload: todo })
   }

   changeTodo = (todo: ITodo) => {
     return ({ type: TodoActionTypes.CHANGE_TODO_STATE_SUCCESS, payload: todo })
   }
}
