import React, { useState } from "react";
import { PopupComponent } from "./popup-component";
import TodoService from "../../service/todoService/TodoService";
import { ITask } from "../../models/interface/interface";

interface PopupContainerProp {
    prevTodoValue: (id: string, body: string) => void
    changeTodoHandler: (changeTask: ITask, id: string) => void
    indexTodoChanged: string
    todoIsCheck: (e: React.ChangeEvent<HTMLInputElement>) => void
}

export const PopupContainer = ({indexTodoChanged, changeTodoHandler}: PopupContainerProp): JSX.Element => {

    const [editTitle, setEditTitle] = useState('')

    const changeTodo = async (body: ITask, id: string): Promise<void> => {
        try {
            const updatedTodo: ITask = await TodoService.updateTodo(body, id)
            changeTodoHandler(body, id)
            console.log(updatedTodo)
        } catch (e) {
            console.log('Error in ChangeTodo function', e)
        }
    };

    const popupInputHandler = (e: any) => {
        const value = e.target.value
        setEditTitle(value)
    }

    const popupBtnHandler = async (e: any) => {
        e.preventDefault()
        const id = indexTodoChanged
        const body: any = {
            id,
            title: editTitle
        }
        console.log(id, body)
        try {
            await changeTodo(body, id)
        } catch (e) {
            console.log('error on changeTodo function', e)
        }
    }

    return (
        <>
        <PopupComponent
            popupBtnHandler={popupBtnHandler}
            popupInputHandler={popupInputHandler}
        />
        </>
    )
}
