import React, { FC } from "react";
import {ITask} from "../../models/interface/interface";
interface TodoFormComponentProp {
    inputPopup: (e: React.ChangeEvent<HTMLInputElement>) => void
    btnTodoSubmit: (e: React.MouseEvent<HTMLButtonElement>) => void
    todo: ITask
}

export const TodoFormComponent: FC<TodoFormComponentProp> = ({inputPopup, btnTodoSubmit, todo}) => {
    return (
        <div className='task-add' key={todo._id}>
            <input type="text" placeholder='Title' onChange={inputPopup}/>
            <button type='submit' onClick={btnTodoSubmit}>Add</button>
        </div>
    )
}
