import React, {useState} from "react";
import TodoService from "../../service/todoService/TodoService";
import { PopupContainer } from '../todo-edit-popup/popup-container';
import { TodosComponent } from "./todos-component";
import { ITask } from "../../models/interface/interface";
import './style.css';

interface TodosContainerProp {
    fetchTodos: () => void
    todos: ITask[]
    setTodos: (body: ITask[]) => void
}

export const TodosContainer = ({todos, setTodos}: TodosContainerProp): JSX.Element => {

    const [changedTodo, setChangedTodos] = useState<ITask[]>([])

    const EditTodo = (title: ITask["title"], id: ITask["_id"]) => {

    }

    const changeTodoHandler = (changeTodo: any) => {
        const {_id} = changeTodo
        console.log(changeTodo.id)
        const newArr = todos.map((todo) => {
            if(changeTodo.id === todo._id) {

                todo.isCheck = !todo.isCheck

                // const prevArr = todos.splice(index, 1, changeTodo)
                // const arr = [...todos] //arr
                // setTodos(arr)
                // console.log(arr)
            } else {
                console.log('Problem in function changeTodoHandler', _id, todo._id)
            }
        })
    }

    const toggleIsCheck = async (body: ITask): Promise<void> => {
        try {
            const updatedTodo = await TodoService.updateTodo(body)
             changeTodoHandler(body)
        } catch (e) {
            console.log('Error in toggleIsCheck function', e)
        }
    };

    const todoIsCheck = async (todo: ITask) => {
        const {_id, title, isCheck} = todo
        console.log(todo)
        const body = {
            id: _id,
            title,
            isCheck: !isCheck
        }
        console.log('I AM BODY', body)
        console.log(body, todos)

        try {
             const response = await toggleIsCheck(body)
            console.log(response)
        } catch (e) {
            console.log(e)
        }
    }

    const changeCheck = (body: ITask) => {
        console.log(body)
    }

    const deleteTodo = async (id: ITask["_id"]): Promise<ITask | void> => {
        try {
            const todo: ITask = await TodoService.deleteTodo(id)
            const newArrTodos = todos.filter(task => {
                return task._id !== todo._id
            })
            setTodos(newArrTodos)
        } catch (e) {
            console.log('Error in function deleteTodo', e)
        }
    }
    const TodoRemove = async (id: ITask["_id"]) => {
        try {
            await deleteTodo(id)
        } catch (e) {
            console.log('Error in function deleteTodo', e)
        }
    }

    return (
        <>
            <TodosComponent todos={todos} TodoRemove={TodoRemove} EditTodo={EditTodo} todoIsCheck={todoIsCheck} />
            <PopupContainer changeTodoHandler={changeTodoHandler} />
        </>
    )
}
