import {ITask} from "../../models/interface/interface";
import axios, {AxiosResponse} from "axios";

const instance = axios.create({
    baseURL: 'http://localhost:8080',
    headers: {'Content-Type': 'application/json'}
})

//todo static class
class TodoService {
     public static getAllTodos = async (): Promise<ITask[] | void> => {
        try {
            const response: AxiosResponse = await instance.get('/allTasks')
            return response.data.data
        } catch (e) {
            console.log('Error GET query', e)
        }
}

   public static createNewTodo = async (body: ITask) => {
         try {
             const response: AxiosResponse = await instance.post('/addTask', body)
             console.log('I AM RESPONSE', response)
             return response.data
         } catch (e) {
             console.log('Error POST query', e)
         }
    }

    deleteTodo = async (id: string) => {
         try {
             const response: AxiosResponse = await instance.delete(`/deleteTask?id=${id}`)
             return response.data
         } catch (e) {
             console.log('Error on DELETE query', e)
         }
    }

    updateTodo = async (body: ITask, id: string) => {
         try {
             const response: AxiosResponse = await instance.patch(`/editTask?id=${id}`, body)
             return response.data
         } catch (e) {
             console.log('Error on PATCH query', e)
         }
    }
}

export default TodoService
