import React, {useEffect, useState} from "react";
import { ITask } from "../../models/interface/interface";
import './style.css'
import {TodosComponent} from "./todos-component";
import TodoService from "../../service/todoService/TodoService";
import TodoForm from "../todo-form/todo-form";


const TodosContainer = (): JSX.Element => {

    const [todos, setTodos] = useState<ITask[]>([])

    const fetchTask = async (): Promise<ITask[] | void> => {
        try {
            const response: any = await new TodoService().getAllTasks()
            setTodos(response)
            console.log(response)
        } catch (e) {
            console.log('qwe', e)
        }
    }
    useEffect(() => {
        fetchTask()
    }, [])

    const newTaskHandler = (newTask: ITask) => {
        setTodos([newTask, ...todos])
    }

    const btnTodoRemove = (deleteTask: any) => {
        console.log(deleteTask.id)
    }
    return (
        <>
            <TodoForm newTaskHandler={newTaskHandler}/>
            <TodosComponent todos={todos} btnTodoRemove={btnTodoRemove}/>
        </>
    )
}
export default TodosContainer
