import { useState } from "react";
import { PopupComponent } from "./popup-component";
import TodoService from "../../service/todoService/TodoService";
import { ITask } from "../../models/interface/interface";

interface PopupContainerProp {
    prevTodoValue: (id: string, body: string) => void
    changeTodoHandler: (changeTask: ITask, id: string) => void
}

export const PopupContainer = ({}: PopupContainerProp): JSX.Element => {

    const [editTitle, setEditTitle] = useState('')


    const changeTodo = async (body: ITask, id: string) => {
        const updatedTodo: ITask = await TodoService.updateTodo(body, id)
        console.log(updatedTodo)
    }

    const popupInputHandler = (e: any) => {
        const value = e.target.value
        setEditTitle(value)
    }

    const prevTodoValue = (id: string, value: string) => {
        const body = {
            _id: id,
            title: value
        }
        console.log(body)
    }

    const popupBtnHandler = (e: any) => {
        e.preventDefault()
        const id = e.target._id
        const body: ITask = {
            _id: id,
            title: editTitle
        }
        console.log(id, body)
    }

    return (
        <>
        <PopupComponent
            popupBtnHandler={popupBtnHandler}
            popupInputHandler={popupInputHandler} />
        </>
    )
}
