import React, { FC, useEffect, useState } from "react";
import { ITask } from "../../models/interface/interface";
import './style.css'
import {TodosComponent} from "./todos-component";
import TodoService from "../../service/todoService/TodoService";
import TodoForm from "../todo-form/todo-form";

interface TodosContainerProp {
    btnTodoRemove: (e: React.MouseEvent<HTMLButtonElement>) => void
}

const TodosContainer = (): JSX.Element => {

    const [todos, setTodos] = useState<ITask[]>([])

    const fetchTask = async (): Promise<ITask[] | void> => {
        try {
            const response: any = await new TodoService().getAllTasks()
            setTodos(response)
            console.log(response)
        } catch (e) {
            console.log('qwe', e)
        }
    }
    useEffect(() => {
        fetchTask()

    }, [])

    const newTaskHandler = (newTask: ITask) => {
        setTodos([newTask, ...todos])
    }

    const deleteTask = async (id: string): Promise<ITask | void> => {
        try{
            const response: any = await new TodoService().deleteTask(id)
            const arr = [todos.splice(response, 0)]
            const newArr = [todos]
            setTodos(newArr)
            console.log(response, arr, newArr)
        } catch (e) {
            console.log('Error on DELETE query', e)
        }
    }

    const btnTodoRemove = (e: any) => {
        e.preventDefault()
        const id = e.target.id
        deleteTask(id)
        console.log(id)
    }
    return (
        <>
            <TodoForm newTaskHandler={newTaskHandler}/>
            <TodosComponent todos={todos} btnTodoRemove={btnTodoRemove}/>
        </>
    )
}
export default TodosContainer
