import {FC, useState} from "react";
import { PopupComponent } from "./popup-component";

export const PopupContainer = (): JSX.Element => {

    const [editTitle, setEditTitle] = useState('')

    const popupInputHandler = (e: any) => {
        const value = e.target.value
        setEditTitle(value)
    }

    const popupBtnHandler = (e: any) => {
        e.preventDefault()
        console.log(editTitle)
    }

    return (
        <>
        <PopupComponent popupBtnHandler={popupBtnHandler} popupInputHandler={popupInputHandler} />
        </>
    )
}
