import React, { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import { TodosContainer } from '../todos-container/todos-container'
import { ITodo } from '../../models/interface/interface'
import { TodoFormContainer } from '../todo-form/todo-form-container'
import { store } from '../../store'
import { TodoActionTypes } from '../../store/types/typesTodo'

// todo project should be clear

export const MainContainer = (): JSX.Element => {
  const dispatch = useDispatch()

  // @ts-ignore
  const { todos }: ITodo[] = store.getState().todos
  const newArr: ITodo[] = todos

  const fetchTodos = () => {
    return (dispatch(({ type: TodoActionTypes.FETCH_TODOS_SUCCESS, payload: [] })))
  }

  useEffect(() => {
    dispatch(fetchTodos())
  }, [])
  console.log(store.getState().todos?.todos)
  return (
            <>
                <TodoFormContainer />
                <TodosContainer
                    fetchTodos={fetchTodos}
                    todos={newArr}
                />
            </>
  )
}
