import React from "react";
import Todo from "../todo/todo";
import {ITask} from "../../models/interface/interface";

interface TodosComponentProp {
    todos: ITask[]
    // btnTodoRemove: (e: React.MouseEvent<HTMLButtonElement>) => void
    btnTodoRemove: (id: ITask["_id"]) => void
    btnEditTodo: (id: ITask["_id"]) => void
    todoIsCheck: (title: ITask["title"], isCheck: ITask["isCheck"], id: ITask["_id"]) => void
}

export const TodosComponent = ({todos, btnTodoRemove, btnEditTodo, todoIsCheck}:TodosComponentProp): JSX.Element  => {
    return (
        <div className='task-container'>
            {todos.map((todo) => {
                return <Todo key={`${todo._id}_${todo.title}`}
                             todo={todo} btnTodoRemove={btnTodoRemove}
                             btnEditTodo={btnEditTodo}
                             todoIsCheck={(title: ITask["title"], isCheck: ITask["isCheck"], id: ITask["_id"]) => todoIsCheck(title, isCheck, id)} />
            })}
        </div>
    )
}
