import { useState } from "react";
import { PopupComponent } from "./popup-component";
import TodoService from "../../service/todoService/TodoService";
import { ITask } from "../../models/interface/interface";

interface PopupContainerProp {
    prevTodoValue: (id: string, body: string) => void
    changeTodoHandler: (changeTask: ITask) => void
    prevValue: string
    indexTodoChanged: string
}

export const PopupContainer = ({prevValue, indexTodoChanged, changeTodoHandler}: PopupContainerProp): JSX.Element => {

    const [editTitle, setEditTitle] = useState('')

    const changeTodo = async (body: ITask) => {
        const updatedTodo: ITask = await TodoService.updateTodo(body)
        console.log(updatedTodo)
    }

    const popupInputHandler = (e: any) => {
        const value = e.target.value
        setEditTitle(value)
    }

    const popupBtnHandler = (e: any) => {
        e.preventDefault()
        const id = indexTodoChanged
        const body: ITask = {
            _id: id,
            title: editTitle
        }
        console.log(id, body)
        changeTodo(body)
        changeTodoHandler(body)
    }

    return (
        <>
        <PopupComponent
            popupBtnHandler={popupBtnHandler}
            popupInputHandler={popupInputHandler}
            prevValue={prevValue}

        />
        </>
    )
}
