import React from 'react'
import TodoService from '../../service/todoService/TodoService'
import { TodosComponent } from './todos-component'
import { ITodo } from '../../models/interface/interface'
import './style.css'
import { useDispatch } from 'react-redux'
// import { changeTodo, deleteBook } from '../../store/action-creators/action-creator'
import { deleteTask, changeTodo } from '../../store/action-creators/action'
import { store } from '../../store'

interface TodosContainerProp {
    fetchTodos: () => void
    todos: ITodo[]
    setTodos: (body: ITodo[]) => void
}

export const TodosContainer = ({ todos, setTodos }: TodosContainerProp): JSX.Element => {
  const dispatch = useDispatch()

  const toggleIsCheck = (todo: ITodo) => {
    const { _id, title, isCheck } = todo
    const body = {
      _id,
      title,
      isCheck: !isCheck
    }
    console.log(body)
    dispatch(changeTodo(body))
  }

  const deleteTodo = (id: ITodo['_id']) => {
    dispatch(deleteTask(id))
    console.log(store.getState())
  }

  return (
        <TodosComponent deleteTodo={deleteTodo} toggleIsCheck={toggleIsCheck} />
  )
}
