import {useEffect, useState} from "react";
import TodosContainer from "../todos-container/todos-container";
import {ITask} from "../../models/interface/interface";
import TodoService from "../../service/todoService/TodoService";

interface Event {
    target: EventTarget | Element
    preventDefault(): void
}

export const MainContainer = ({target}:Event): JSX.Element => {

    const [todos, setTodos] = useState<ITask[]>([])

    const [prevValueTitle, setTodoTitlePrev] = useState<string>('')
    const [indexTodoChanged, setTodoId] = useState<string>('')

    const fetchTodos = async (): Promise<void> => {
        try {
            //todo typisation utility types
            const getTodos: any = await TodoService.getAllTodos()
            console.log(getTodos)
            setTodos(getTodos)
        } catch (e) {
            console.log('Error in fetchTodos', e)
        }
    }


    const newTaskHandler = (newTask: ITask) => {
        setTodos([newTask, ...todos])
    }

    const changeTodoHandler = (changeTodo: ITask, id: string) => {
        todos.map((todo, index) => {
            if(id === todo._id) {
                const prevArr = todos.splice(index, 1, changeTodo)
                const arr = [...todos]
                setTodos(arr)
            } else {
                console.log('не сработало')
            }
        })
    }

    const deleteTodo = async (id: string): Promise<ITask | void> => {
        try {
            const todo: ITask = await TodoService.deleteTodo(id)
            const newArrTodos = todos.filter(task => {
                return task._id !== todo._id
            })
            setTodos(newArrTodos)
        } catch (e) {
            console.log('Error in function deleteTodo', e)
        }
    }
    const btnTodoRemove = async (event: Event) => {
        event.preventDefault()
        if ("id" in event.target) {
            const id = event.target.id
            try {
                await deleteTodo(id)
            } catch (e) {
                console.log('Error in function deleteTodo', e)
            }
            console.log(id)
        }
    }

    const btnEditTodo = (e: Event) => {
        e.preventDefault()
        if('id' in e.target) {
            const id = e.target.id
            todos.forEach(todo => {
                if (todo._id === id) {
                    const value = todo.title
                    prevTodoValue(id, value)
                    setTodoTitlePrev(value)
                    setTodoId(id)
                }
            })
        }
    }

    const prevTodoValue = (id: string, value: string) => {
        const body = {
            _id: id,
            title: value
        }
        console.log(body)
    }

    return (
        <>
            <TodosContainer fetchTodos={fetchTodos}
                            todos={todos}
                            btnTodoRemove={btnTodoRemove}
                            btnEditTodo={btnEditTodo}
                            prevTodoValue={prevTodoValue}
                            prevValueTitle={prevValueTitle}
                            changeTodoHandler={changeTodoHandler}
                            newTaskHandler={newTaskHandler}
            />

        </>
    )
}
