import React, {FC} from "react";
import { ITask } from "../../models/interface/interface";
import './style.css'

interface TaskProps {
    todo: ITask
    btnTodoRemove: (e: React.MouseEvent<HTMLButtonElement>) => void
    btnEditTodo: (e: React.MouseEvent<HTMLButtonElement>) => void
    todoIsCheck: (e: React.ChangeEvent<HTMLInputElement>) => void
}

const Task: FC<TaskProps> = ({todo, btnTodoRemove, btnEditTodo, todoIsCheck}) => {
    return (
            <div className='task' key={todo._id}>
                    <input type="checkbox" onChange={todoIsCheck}  checked={todo.isCheck} id={todo._id} value={todo.title} key={todo._id} />
                    <p>{todo.title}</p>
                    <button className='btn-edit' id={todo._id} type='button' key={todo._id} onClick={btnEditTodo}>Edit</button>
                    <button id={todo._id} className='btn-trash' onClick={btnTodoRemove}>Remove</button>
            </div>
    )
}

export default Task
