import { TodoAction, TodoActionTypes } from '../types/typesTodo'
import { Dispatch } from 'redux'
import TodoService from '../../service/todoService/TodoService'
import { ITodo } from '../../models/interface/interface'

export const fetchBooks = () => {
  return async (dispatch: Dispatch<TodoAction>) => {
    try {
      dispatch({ type: TodoActionTypes.FETCH_TODOS })
      const todo = await TodoService.getAllTodos()
      dispatch({ type: TodoActionTypes.FETCH_TODOS_SUCCESS, payload: todo })
      console.log(todo)
    } catch (e) {
      console.log(e)
    }
  }
}

export const createBook = (book: ITodo) => {
  return async (dispatch: Dispatch<TodoAction>) => {
    try {
      dispatch({ type: TodoActionTypes.CREATE_TODO })
      const createdBook = await TodoService.createNewTodo(book)
      dispatch({ type: TodoActionTypes.CREATE_TODO_SUCCESS, payload: createdBook })
    } catch (e) {
      console.log('Error in createBook', e)
    }
  }
}

export const deleteBook = (index: ITodo['_id']) => {
  return async (dispatch: Dispatch<TodoAction>) => {
    try {
      const deleteBook = await TodoService.deleteTodo(index)
      dispatch({ type: TodoActionTypes.DELETE_TODO_SUCCESS, payload: deleteBook })
    } catch (e) {
      console.log('Error in function deleteBook', e)
    }
  }
}

export const changeTodoState = (todo: ITodo) => {
  return async (dispatch: Dispatch<TodoAction>) => {
    try {
      const changedTodoState = await TodoService.updateTodo(todo)
      dispatch({ type: TodoActionTypes.CHANGE_TODO_STATE, payload: changedTodoState })
    } catch (e) {
      console.log('Error in changeTodoState function', e)
    }
  }
}
