import { useState } from "react";
import { PopupComponent } from "./popup-component";
import TodoService from "../../service/todoService/TodoService";
import { ITask } from "../../models/interface/interface";

interface PopupContainerProp {
    changeTodoHandler: (changeTask: ITask, id: ITask["_id"]) => void
    idChangeableTodo: ITask["_id"]
}

export const PopupContainer = ({ changeTodoHandler, idChangeableTodo }: PopupContainerProp): JSX.Element => {

    const [editedTitle, setEditTitle] = useState('')

    const changeTodo = async (body: ITask, id: ITask["_id"]): Promise<void> => {
        try {
            const updatedTodo: ITask = await TodoService.updateTodo(body, id)

            console.log(updatedTodo)
        } catch (e) {
            console.log('Error in ChangeTodo function', e)
        }
    };

    const popupInputHandler = (e: any) => {
        const value = e.target.value
        setEditTitle(value)
    }

    const popupBtnHandler = async () => {
        const value = editedTitle
        const body = {
            _id: idChangeableTodo,
            title: value
        }
        console.log(body)
        try {
            await changeTodo(body, idChangeableTodo)
            changeTodoHandler(body, idChangeableTodo)

        } catch (e) {console.log('error on changeTodo function', e)}
    }

    return (
        <>
        <PopupComponent
            popupBtnHandler={popupBtnHandler}
            popupInputHandler={popupInputHandler} />
        </>
    )
}
