import React, {useEffect, useState} from "react";
import './style.css'
import {ITask} from "../interface/interface";
import TaskApi from "../TaskApi/TaskApi";
const TaskAdd = () => {

    const [input, setInput] = useState('')
    const [task, setTasks] = useState<ITask[]>([])

    useEffect(() => {
        const fetchTask = async (): Promise<ITask[] | void> => {
            try {
                const response: any = await new TaskApi().getAllTasks()
                setTasks(response)
            } catch (e) {
                console.log('qwe', e)
            }
        }
        fetchTask()
    }, [setInput])

    const createTask = async (body: ITask) => {
        try {
            const response = await new TaskApi().createNewTask(body)
            console.log(response)

        } catch (e) {
            console.log('Error on POST query', e)
        }
    }

    const changeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value
        setInput(value)
    }

    const clickHandler = (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault()
        const body: ITask = {
            title: input,
            isCheck: false
        }
        createTask(body)
    }
    return (
        <div className='task-add'>
            <input type="text" placeholder='Title' onChange={changeHandler}/>
            <button type='submit' onClick={clickHandler}>Add</button>
        </div>
    )
}
export default TaskAdd
