import React, { FC, useState } from 'react'
import TodoService from '../../service/todoService/TodoService'
import { ITodo } from '../../models/interface/interface'
import { TodoFormComponent } from './todo-form-component'
// import { createBook } from '../../store/action-creators/action-creator'
import './style.css'
import { useDispatch, useSelector } from 'react-redux'
import { createTodo } from '../../store/action-creators/action'
import { store } from '../../store'
import { fetchTodos } from '../../store/action-creators/action-creator'

// interface TodoFormProp {
//     newTaskHandler: (task: ITodo) => void
// }

export const TodoFormContainer = (/* { newTaskHandler }: TodoFormProp */): JSX.Element => {
  const [inputValue, setInputValue] = useState<string>('')
  const dispatch = useDispatch()

  // const createTodo = async (body: ITodo) => {
  //   try {
  //     const todo: ITodo = await TodoService.createNewTodo(body)
  //     newTaskHandler(todo)
  //   } catch (e) {
  //     console.log('Error in function createTask', e)
  //   }
  // }

  const setInputValueHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value
    setInputValue(value)
  }

  const todoSubmit = (e: React.MouseEvent<HTMLButtonElement>) => {
    e.preventDefault()

    if (inputValue.trim() === '') {
      return
    }

    const body = {
      title: inputValue,
      isCheck: false
    }
    // dispatch(createBook(body))
    dispatch(createTodo(body))
    dispatch(fetchTodos())
    console.log(createTodo(body))
    console.log(store.getState())
    setInputValue('')
    // try {
    //   await TodoService.createNewTodo(body)
    // } catch (e) {
    //   console.log('Error in function createTask')
    // }
  }

  return (
         <TodoFormComponent
            inputValue={inputValue}
            setInputValueHandler={setInputValueHandler}
            todoSubmit={todoSubmit}
         />
  )
}
