import {ITask} from "../../components/interface/interface";

import axios, {AxiosResponse} from "axios";

axios.defaults.baseURL = 'http://localhost:8080'
axios.defaults.headers.common['Content-Type'] = 'application/json'


class TodoService {

     getAllTasks = async (): Promise<ITask[] | void> => {
        try {
            const response: AxiosResponse = await axios.get('/allTasks')
            return await response.data.data
        } catch (e) {
            console.log('Error GET query', e)
        }
}
    createNewTask = async (body: ITask) => {
         try {
             const response: AxiosResponse = await axios.post('/addTask', body)
             console.log('I AM RESPONSE', response)
             return response.data
         } catch (e) {
             console.log('Error POST query', e)
         }
    }
}

export default TodoService
