import React, { useState } from "react";
import { ITask } from "../../models/interface/interface";
import TodoService from "../../service/todoService/TodoService";
import { TodoFormComponent } from "./todo-form-component";
import './style.css'

interface TodoFormProp {
    newTaskHandler: (task: ITask) => void
}

export const TodoForm = ({ newTaskHandler }: TodoFormProp) => {

    const [input, setInput] = useState('')

    const createTask = async (body: ITask) => {
        try {
            const task: ITask = await TodoService.createNewTodo(body)
            newTaskHandler(task)
        } catch (e) {
            console.log('Error in function createTask', e)
        }
    }

    const inputPopup = (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value
        setInput(value)
    }

    const btnTodoSubmit = async (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault()
        if(input.trim() === '') {
            return;
        }
        const body: ITask = {
            title: input,
            isCheck: false
        }
        try {
            await createTask(body)
        } catch (e) {
            console.log('Error in function createTask')
        }
    }

    return (
         <TodoFormComponent
             inputPopup={inputPopup}
            btnTodoSubmit={btnTodoSubmit}
         />
    )
}

export default TodoForm
