import {ITask} from "./components/interface/interface";
import TodoForm from "./components/todo-form/todo-form";
import TodosContainer from "./components/todos-container/todos-container";
import TaskApi from "./components/TaskApi/TaskApi";


const App = () => {

  const [tasks, setTasks] = useState<ITask[]>([])

    const fetchTask = async (): Promise<ITask[] | void> => {
        try {
            const response: any = await new TaskApi().getAllTasks()
            setTasks(response)
            console.log(response)
        } catch (e) {
            console.log('qwe', e)
        }
    }

  useEffect(() => {
      fetchTask()
  }, [])

    //todo handler for new todo
    const newTaskHandler = (newTask: ITask) => {
      setTasks([newTask, ...tasks])
    }

  return (
    <div>

      <TodosContainer/>
    </div>

  );
}
export default App;
