}

const Task = ({ todo, deleteTodo, toggleIsCheck }: TodoProps): JSX.Element => {
  return (
    <div className='task'>
        <input type="checkbox" onChange={() => toggleIsCheck(todo)} checked={todo.isCheck} value={todo.title} />
        <p>{todo.title}</p>
        <button id={todo._id} className='btn-trash' onClick={() => /* deleteTodo(todo._id) */ deleteTodo(todo) }>Remove</button>
    </div>
  )
}

