import React, {FC} from "react";
import { ITask } from "../../models/interface/interface";
import './style.css'

//todo refactor name of functions

interface TaskProps {
    todo: ITask
    TodoRemove: (id: ITask["_id"]) => void
    todoIsCheck: (todo: ITask) => void
}

const Task: FC<TaskProps> = ({todo, TodoRemove, todoIsCheck}) => {
    return (
            <div className='task'>
                    <input type="checkbox" onChange={() => todoIsCheck(todo)} checked={todo.isCheck} value={todo.title} />
                    <p>{todo.title}</p>
                    <button className='btn-edit' id={todo._id} type='button'>Edit</button>
                    <button id={todo._id} className='btn-trash' onClick={() => TodoRemove(todo._id)}>Remove</button>
            </div>
    )
}

export default Task
