import React, {FC, useEffect, useState} from "react";
import './style.css'
import {ITask} from "../interface/interface";
import TaskApi from "../TaskApi/TaskApi";

interface TaskAddProp {
    newTaskHandler
}

const TaskAdd = (newTaskHandler: any) => {


    const [input, setInput] = useState('')

    const createTask = async (body: ITask) => {
        try {
            const task: ITask = await new TaskApi().createNewTask(body)
            console.log(task)
            //todo call handler here
            newTaskHandler(task)
            // setNewTask(task)
        } catch (e) {
            console.log('Error on POST query', e)
        }
    }

    const changeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value
        setInput(value)
    }

    const clickHandler = (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault()
        const body: ITask = {
            title: input,
            isCheck: false
        }
        createTask(body)
    }
    return (
        <div className='task-add'>
            <input type="text" placeholder='Title' onChange={changeHandler}/>
            <button type='submit' onClick={clickHandler}>Add</button>
        </div>
    )
}
export default TaskAdd
