import React from 'react'
import { TodosComponent } from './todos-component'
import { ITodo } from '../../models/interface/interface'
import './style.css'
import { useDispatch } from 'react-redux'
import { deleteTask, changeTodo } from '../../store/action-creators/action'
import { store } from '../../store'

interface TodosContainerProp {
    fetchTodos: () => void
}

export const TodosContainer = (): JSX.Element => {
  const dispatch = useDispatch()

  const toggleIsCheck = (todo: ITodo) => {
    const { _id, title, isCheck } = todo
    const body = {
      _id,
      title,
      isCheck: !isCheck
    }
    console.log(body)
    dispatch(changeTodo(body))
  }

  const deleteTodo = async (id: ITodo['_id']): Promise<void> => {
    try {
      dispatch(deleteTask(id))
      console.log(store.getState())
    } catch (e) {
      console.log('Error in function deleteTodo', e)
    }
  }

  return (
        <TodosComponent deleteTodo={deleteTodo} toggleIsCheck={toggleIsCheck} />
  )
}
